```		func TestParsingResponse(t *testing.T) {
	jsonData := `{"summary":"Test summary","walkthrough":[{"files":"file.go","summary":"Changed logic"}],"haiku":"A test haiku"}`
	summary, err := ParseSummary(jsonData)
	if err != nil {
		t.Fatalf("expected no error, got %v", err)
	}
	if summary.Summary != "Test summary" {
		t.Errorf("expected summary to be 'Test summary', got '%s'", summary.Summary)
	}
	if len(summary.Walkthrough) != 1 {
		t.Errorf("expected 1 walkthrough entry, got %d", len(summary.Walkthrough))
	}
	if summary.Haiku != "A test haiku" {
		t.Errorf("expected haiku to be 'A test haiku', got '%s'", summary.Haiku)
	}

	// Test invalid JSON
	_, err = ParseSummary("not a json")
	if err == nil {
		t.Error("expected error for invalid JSON, got nil")
	}
}```
